validate.script:
$agentService = Get-Service -Name CSAgent -ErrorAction SilentlyContinue
if ($agentService) {
    Write-Output 'Falcon Sensor already installed... if you want to update or downgrade, please use Sensor Update Policies in the CrowdStrike console. Please see: https://falcon.crowdstrike.com/documentation/66/sensor-update-policies for more information.'
    Exit 100
}
Exit 101

enforce.script:
$installArguments = @({{ .WindowsInstallParams }})
$installerProcess = Start-Process -FilePath "C:\\Windows\\SystemTemp\\falcon-sensor.exe" -ArgumentList $installArguments -PassThru -Wait

if ($installerProcess.ExitCode -ne 0) {
    Write-Output "Installer returned exit code $($installerProcess.ExitCode)"
    Exit 101
}

$agentService = Get-Service -Name CSAgent -ErrorAction SilentlyContinue
if (-not $agentService) {
    Write-Output 'Installer completed, but CSAgent service is missing...'
    Exit 101
}
elseif ($agentService.Status -eq 'Running') {
    Write-Output 'CSAgent service running...'
    Exit 100
}
else {
    Write-Output 'Installer completed, but CSAgent service is not running...'
    Exit 101
}
